mapper.delete:
DELETE FROM TB_COMMENT
WHERE COMMENT_ID = #{commentId}
    AND (USER_ID = #{userId} OR #{userRole} = 'ADMIN')

mapper.update:
UPDATE TB_COMMENT
SET COMMENT_TEXT = #{commentText},
    UPDATED_DATE = SYSDATE,
    UPDATED_ID = #{userId}
WHERE COMMENT_ID = #{commentId}
AND USER_ID = #{userId}
